sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
//Envia Request a Jira
    try {
		var instanceName = gs.getProperty('instance_name');
        var r = new sn_ws.RESTMessageV2('Jira Beta Integration', 'POST');	
        //Campos
        var fields = [];
        fields = {};
        fields.fields = {};
        fields.fields.project = {};
        fields.fields.project.id = '10000';
        fields.fields.summary =  ''+ current.number + ' - ' + current.short_description;
        fields.fields.description = '' + current.task.description;
        fields.fields.issuetype = {};
        fields.fields.issuetype.id = '10003';
		fields.fields.customfield_10058 = ''+current.assignment_group.getDisplayValue();
		fields.fields.customfield_10059 = ''+current.requested_for.getDisplayValue();
        fields.fields.customfield_10060 = ''+current.number;
		fields.fields.customfield_10061 = '' + current.request.toString();
		//Verificação de Número de Task
		var solicitacao = new GlideRecord('sc_req_item');
		solicitacao.addQuery('number', current.number);
		solicitacao.query();
		while (solicitacao.next()) {
		var numberSolicitacao = solicitacao.sys_id;
		}
		gs.log('RITM: ' + numberSolicitacao);

		var gr = new GlideRecord('sc_task');
		gr.addQuery('parent',numberSolicitacao);
		gr.query();
		while (gr.next()) {
		var taskId = gr.request_item.getDisplayValue();
		var taskNumber = gr.task_effective_number;
		}
		gs.log('TASK: ' + gr.number);
		// Fim da verificação de número de Task

		fields.fields.customfield_10063 = '' + gr.number;
		gs.log('Integration foi ' + taskId + taskNumber + ' e ' + numberSolicitacao);
        var json = JSON.stringify(fields);
        //Fim dos campos
        r.setRequestBody(json);
        var response = r.execute();
        var httpStatus = response.getStatusCode();

        //comentário de integração.
        gs.log('Entrou na integração : ' + httpStatus + '' + json);
		current.work_notes = 'Integração Jira Iniciada.';
		current.setWorkflow(false); 
		current.update();
    } catch (ex) {
        var message = ex.message;
		gs.log('Não Entrou na integração : ' + httpStatus + '' + json);
    }

})(current, previous);
